mapper.select:
select 

from remain_data r
where r.id = #{id,jdbcType=INTEGER}

select 

from remain_data r
where 1=1 
and r.token= #{token,jdbcType=VARCHAR}
and r.add_active_device= #{addActiveDevice,jdbcType=BIGINT}
and r.add_inactive_device= #{addInactiveDevice,jdbcType=BIGINT}
and r.remain_active_device= #{remainActiveDevice,jdbcType=BIGINT}
and r.remain_inactive_device= #{remainInactiveDevice,jdbcType=BIGINT}
and r.remain_path= #{remainPath,jdbcType=VARCHAR}
and r.create_date= #{createDate,jdbcType=TIMESTAMP}
limit #{pageStart,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select 

from remain_data r
where 1=1 
and r.token= #{token,jdbcType=VARCHAR}
and r.add_active_device= #{addActiveDevice,jdbcType=BIGINT}
and r.add_inactive_device= #{addInactiveDevice,jdbcType=BIGINT}
and r.remain_active_device= #{remainActiveDevice,jdbcType=BIGINT}
and r.remain_inactive_device= #{remainInactiveDevice,jdbcType=BIGINT}
and r.remain_path= #{remainPath,jdbcType=VARCHAR}
and r.create_date= #{createDate,jdbcType=TIMESTAMP}

select 

from remain_data r
where 1=1 
and r.token= #{model.token,jdbcType=VARCHAR}
and r.add_active_device= #{model.addActiveDevice,jdbcType=BIGINT}
and r.add_inactive_device= #{model.addInactiveDevice,jdbcType=BIGINT}
and r.remain_active_device= #{model.remainActiveDevice,jdbcType=BIGINT}
and r.remain_inactive_device= #{model.remainInactiveDevice,jdbcType=BIGINT}
and r.remain_path= #{model.remainPath,jdbcType=VARCHAR}
and r.create_date= #{model.createDate,jdbcType=TIMESTAMP}
 limit #{page.pageStart,jdbcType=INTEGER},#{page.pageSize,jdbcType=INTEGER}

select count(0) totalCount
from remain_data r
where 1=1 
and r.token= #{token,jdbcType=VARCHAR}
and r.add_active_device= #{addActiveDevice,jdbcType=BIGINT}
and r.add_inactive_device= #{addInactiveDevice,jdbcType=BIGINT}
