mapper.select:
select 

from remain_data r
where r.id = #{id,jdbcType=BIGINT}

select 

from remain_data r
where 1=1 
and r.app_id= #{appId,jdbcType=BIGINT}
and r.create_date BETWEEN #{createDate,jdbcType=TIMESTAMP} AND #{updateDate,jdbcType=TIMESTAMP}

select 

from remain_data r
where 1=1
and r.app_id= #{appId,jdbcType=BIGINT}
and r.active= #{active,jdbcType=BIGINT}
and r.inactive= #{inactive,jdbcType=BIGINT}
and r.remain= #{remain,jdbcType=BIGINT}
and r.remain_active= #{remainActive,jdbcType=BIGINT}
and r.unremain= #{unremain,jdbcType=BIGINT}
and r.remain_path= #{remainPath,jdbcType=VARCHAR}
and r.create_date= #{createDate,jdbcType=TIMESTAMP}
and r.update_date= #{updateDate,jdbcType=TIMESTAMP}
order by r.create_date desc

select 

from remain_data r
where 1=1 
and r.app_id= #{appId,jdbcType=BIGINT}
and r.active= #{active,jdbcType=BIGINT}
and r.inactive= #{inactive,jdbcType=BIGINT}
and r.remain= #{remain,jdbcType=BIGINT}
