and r.unremain= #{unremain,jdbcType=BIGINT}
and r.remain_path= #{remainPath,jdbcType=VARCHAR}
and r.create_date= #{createDate,jdbcType=TIMESTAMP}
and r.update_date= #{updateDate,jdbcType=TIMESTAMP}

select 

from remain_data r
where 1=1 
and r.app_id= #{model.appId,jdbcType=BIGINT}
and r.active= #{model.active,jdbcType=BIGINT}
and r.inactive= #{model.inactive,jdbcType=BIGINT}
and r.remain= #{model.remain,jdbcType=BIGINT}
and r.remain_active= #{model.remainActive,jdbcType=BIGINT}
and r.unremain= #{model.unremain,jdbcType=BIGINT}
and r.remain_path= #{model.remainPath,jdbcType=VARCHAR}
and r.create_date= #{model.createDate,jdbcType=TIMESTAMP}
and r.update_date= #{model.updateDate,jdbcType=TIMESTAMP}
 limit #{page.pageStart,jdbcType=INTEGER},#{page.pageSize,jdbcType=INTEGER}

select count(0) totalCount
from remain_data r
where 1=1 
and r.app_id= #{appId,jdbcType=BIGINT}
and r.active= #{active,jdbcType=BIGINT}
and r.inactive= #{inactive,jdbcType=BIGINT}
